        password: "password2",
        role: "TechOps",
      },
      {
        id: 1,
        firstName: "John",
        lastName: "Doe",
        email: "@esko.com",
        password: "password1",
        role: "DevOps",
      },
      {
        id: 2,
        firstName: "Jane",
        lastName: "Smith",
        email: "@esko.com",
        password: "password2",
        role: "TechOps",
      },
      {
        id: 1,
        firstName: "John",
        lastName: "Doe",
        email: "@esko.com",
        password: "password1",
        role: "DevOps",
      },
      {
        id: 2,
        firstName: "Jane",
        lastName: "Smith",
        email: "@esko.com",
        password: "password2",
        role: "TechOps",
      },
      {
        id: 1,
        firstName: "John",
