import React, { useState } from 'react';
import './EditUserPopup.css';

const EditUserPopup = ({ user, onSave, onCancel }) => {
  const [editedUser, setEditedUser] = useState({ ...user });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEditedUser(prevUser => ({
      ...prevUser,
      [name]: value
    }));
  };

  const handleSave = () => {
    onSave(editedUser);
  };

  return (
    <div className="edit-user-popup">
      <h2>Edit User</h2>
      <form>
        <label>
          Associate ID:
          <input type="text" name="associate_id" value={editedUser.associate_id} onChange={handleChange} />
        </label>
        <label>
        Associate Name:
          <input type="text" name="associate_name" value={editedUser.associate_name} onChange={handleChange} />
        </label>
        <label>
          Email:
          <input type="email" name="email" value={editedUser.email} onChange={handleChange} />
        </label>
        <div className="buttons">
          <button type="button" onClick={handleSave}>Save</button>
          <button type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default EditUserPopup;


