import React, { useState } from "react";
import axios from "axios";
import "./AddNewUser.css"; // Import CSS for AddNewUser page

const AddNewUser = () => {
    const [formData, setFormData] = useState({
        associateName: "",
        associateId: "",
        localSystemId: "",
        email: "",
        managerId: "",
        managerEmail: "",
        managerName: "",
        directReport: "",
        company: "",
        opco: "",
    });

    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setLoading(true); // Set loading to true when submitting the form

        axios
            .post("http://localhost:3000/admin/add-user", formData)
            .then((response) => {
                console.log("User data saved successfully:", response.data);
                setMessage("User added successfully!"); // Set success message
                // Clear input fields after successful submission
                setFormData({
                    associateName: "",
                    associateId: "",
                    localSystemId: "",
                    email: "",
                    managerId: "",
                    managerEmail: "",
                    managerName: "",
                    directReport: "",
                    company: "",
                    opco: "",
                });
            })
            .catch((error) => {
                console.error("Error saving user data:", error);
                setMessage("Error adding user. Please try again."); // Set error message
            })
            .finally(() => {
                setLoading(false); // Set loading back to false after submission
                setTimeout(() => {
                    setMessage("");
                }, 3000);
            });
       
    };

    return (
        <div className="add-user-page">
            <h2 className="add-user-header">Add New User</h2>
            <form onSubmit={handleSubmit} className="add-user-form">
                <div className="form-group">
                    <input
                        type="text"
                        name="associateName"
                        value={formData.associateName}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="associateId"
                        value={formData.associateId}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="localSystemId"
                        value={formData.localSystemId}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="managerId"
                        value={formData.managerId}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="email"
                        name="managerEmail"
                        value={formData.managerEmail}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="managerName"
                        value={formData.managerName}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="directReport"
                        value={formData.directReport}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="company"
                        value={formData.company}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        name="opco"
                        value={formData.opco}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit" className="submit-button">
                    {loading ? "Adding User..." : "Add User"}
                </button>
            </form>
            {message && (
                <div
                    className={`message ${message.startsWith("Error") ? "error" : "success"
                        }`}
                >
                    {message}
                </div>
            )}
        </div>
    );
};

export default AddNewUser;
