import React, { useState, useEffect } from "react";
import "./BookSeatPage.css"; // Import CSS for BookSeatPage
import axios from "axios";

const BookSeatPage = () => {
  // State variables for form fields
  const [userName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [fromTime, setFromTime] = useState("");
  const [toTime, setToTime] = useState("");

  useEffect(() => {
    // Fetch user details from the backend API (to be implemented later)
    axios
      .get("http://localhost:3000/api/user-details")
      .then((response) => {
        const userData = response.data;
        setUserName(userData.name);
        setEmail(userData.email);
      })
      .catch((error) => {
        console.error("Error fetching user details:", error);
      });
  }, []);

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    // Placeholder for saving form data to backend (to be implemented later)
    console.log("Form submitted:", {
      userName,
      email,
      phone,
      fromDate,
      toDate,
      fromTime,
      toTime,
    });
  };

  // Function to format time in 12-hour format with "A.M." and "P.M."
  const formatTime = (timeString) => {
    return new Date(`2000-01-01T${timeString}`).toLocaleTimeString([], { hour: "2-digit", minute: "2-digit", hour12: true });
  };

  return (
    <div className="book-seat-container">
      <h2>Book Your Seat</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="userName">User Name:</label>
          <input type="text" id="userName" value={userName} readOnly />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" value={email} readOnly />
        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone:</label>
          <input
            type="text"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="fromDate">From Date:</label>
          <input
            type="date"
            id="fromDate"
            min={new Date().toISOString().split("T")[0]}
            value={fromDate}
            onChange={(e) => setFromDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="toDate">To Date:</label>
          <input
            type="date"
            id="toDate"
            min={fromDate}
            value={toDate}
            onChange={(e) => setToDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="fromTime">From Time:</label>
          <input
            type="time"
            id="fromTime"
            value={fromTime}
            onChange={(e) => setFromTime(e.target.value)}
            required
          />
          <span className="time-format">{formatTime(fromTime)}</span>
        </div>
        <div className="form-group">
          <label htmlFor="toTime">To Time:</label>
          <input
            type="time"
            id="toTime"
            value={toTime}
            onChange={(e) => setToTime(e.target.value)}
            required
          />
          <span className="time-format">{formatTime(toTime)}</span>
        </div>
        <button type="submit">Book Seat</button>
      </form>
    </div>
  );
};

export default BookSeatPage;
