import {
  Drawer,
  Hidden,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  makeStyles,
  Paper,
} from "@material-ui/core";
import { Home } from "@material-ui/icons";
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
import clsx from "clsx";
import React from "react";
import { Fragment } from "react";
import AppsIcon from "@material-ui/icons/Apps";
import { Link } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
  root: {
    height: "100%",
    overflowY: "auto",
  },
  list: {
    width: 250,
  },
  fullList: {
    width: "auto",
  },
  listItem: {
    transition: "background-color 0.3s, box-shadow 0.3s",
  },
  activeListItem: {
    backgroundColor: theme.palette.primary.main, // Change to your desired active background color
    color
  },
  listItemHover: {
    "&:hover": {
      backgroundColor: theme.palette.grey[200], // Change to your desired hover background color
      boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)", // Change to your desired box shadow
    },
  },
}));

function NavBar(props) {
  const { openMenu, closeMenu, className, ...rest } = props;
  const classes = useStyles();

  return (
    <Fragment>
      <Hidden lgUp>
        <Drawer
          anchor="left"
          variant="temporary"
          onClose={closeMenu}
          open={openMenu}
        >
          <div {...rest} className={clsx(classes.root, className)}></div>
        </Drawer>
      </Hidden>
      <Hidden mdDown>
        <Paper
          {...rest}
          className={clsx(classes.root, className)}
          elevation={1}
          square
        >
          <List>
            <ListItem
              component={Link}
              to="/"
              className={clsx(
                classes.listItem,
                classes.listItemHover,
                window.location.pathname === "/" && classes.activeListItem
              )}
            >
              <ListItemIcon>
                <Home />
              </ListItemIcon>
              <ListItemText>Home</ListItemText>
            </ListItem>
            <ListItem
              component={Link}
              to="/table"
              className={clsx(
                classes.listItem,
                classes.listItemHover,
                window.location.pathname === "/table" && classes.activeListItem
              )}
            >
              <ListItemIcon>
                <AppsIcon />
              </ListItemIcon>
              <ListItemText>Table</ListItemText>
            </ListItem>
            <ListItem
              component={Link}
              to="/usermanagement"
              className={clsx(
                classes.listItem,
                classes.listItemHover,
                window.location.pathname === "/usermanagement" && classes.activeListItem
              )}
            >
              <ListItemIcon>
                <PeopleAltIcon />
              </ListItemIcon>
              <ListItemText>Manage User</ListItemText>
            </ListItem>
          </List>
        </Paper>
      </Hidden>
    </Fragment>
  );
}

export default NavBar;
