import React, { useState, useEffect } from "react";
import "./BookSeatPage.css"; // Import CSS for BookSeatPage
import axios from "axios";

const BookSeatPage = () => {
  // State variables for form fields
  const [userName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  useEffect(() => {
    // Fetch user details from the backend API (to be implemented later)
    axios.get("http://localhost:3000/api/user-details")
      .then((response) => {
        const userData = response.data;
        setUserName(userData.name);
        setEmail(userData.email);
      })
      .catch((error) => {
        console.error("Error fetching user details:", error);
      });
  }, []);

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    // Placeholder for saving form data to backend (to be implemented later)
    console.log("Form submitted:", { userName, email, phone, date, time });
  };

  return (
    <div className="book-seat-container">
      <h2>Book Your Seat</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="userName">User Name:</label>
          <input
            type="text"
            id="userName"
            value={userName}
            readOnly
          />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            value={email}
            readOnly
          />
        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone:</label>
          <input
            type="text"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="date">Date:</label>
          <input
            type="date"
            id="date"
            min={new Date().toISOString().split("T")[0]}
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="time">Time:</label>
          <input
            type="time"
            id="time"
            value={time}
            onChange={(e) => setTime(e.target.value)}
            required
          />
        </div>
        <button type="submit">Book Seat</button>
      </form>
    </div>
  );
};

export default BookSeatPage;
