import React, { useState, useEffect } from "react";
import "./BookSeatPage.css"; // Import CSS for BookSeatPage
import axios from "axios";

const BookSeatPage = () => {
  // State variables for form fields
  const [userName, setUserName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [fromDate, setFromDate] = useState("");
  const [toDate, setToDate] = useState("");
  const [fromHour, setFromHour] = useState("01");
  const [fromMinute, setFromMinute] = useState("00");
  const [fromTimePeriod, setFromTimePeriod] = useState("AM");
  const [toHour, setToHour] = useState("01");
  const [toMinute, setToMinute] = useState("00");
  const [toTimePeriod, setToTimePeriod] = useState("AM");

  // Function to format the selected time
  const formatTime = (hour, minute, timePeriod) => {
    return `${hour}:${minute} ${timePeriod}`;
  };

  // Function to update selected time
  const updateSelectedTime = () => {
    const fromTime = formatTime(fromHour, fromMinute, fromTimePeriod);
    const toTime = formatTime(toHour, toMinute, toTimePeriod);
    return { fromTime, toTime };
  };

  // Update selected time whenever any time input changes
  useEffect(() => {
    const { fromTime, toTime } = updateSelectedTime();
    setFromSelectedTime(fromTime);
    setToSelectedTime(toTime);
  }, [fromHour, fromMinute, fromTimePeriod, toHour, toMinute, toTimePeriod]);

  // State variables for selected times
  const [fromSelectedTime, setFromSelectedTime] = useState("");
  const [toSelectedTime, setToSelectedTime] = useState("");

  useEffect(() => {
    // Fetch user details from the backend API (to be implemented later)
    axios
      .get("http://localhost:3000/api/user-details")
      .then((response) => {
        const userData = response.data;
        setUserName(userData.name);
        setEmail(userData.email);
      })
      .catch((error) => {
        console.error("Error fetching user details:", error);
      });
  }, []);

  // Function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    // Placeholder for saving form data to backend (to be implemented later)
    console.log("Form submitted:", {
      userName,
      email,
      phone,
      fromDate,
      toDate,
      fromHour,
      fromMinute,
      fromTimePeriod,
      toHour,
      toMinute,
      toTimePeriod,
    });
  };

  return (
    <div className="book-seat-container">
      <h2>Book Your Seat</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="userName">User Name:</label>
          <input type="text" id="userName" value={userName} readOnly />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input type="email" id="email" value={email} readOnly />
        </div>
        <div className="form-group">
          <label htmlFor="phone">Phone:</label>
          <input
            type="text"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="fromDate">From Date:</label>
          <input
            type="date"
            id="fromDate"
            min={new Date().toISOString().split("T")[0]}
            value={fromDate}
            onChange={(e) => setFromDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="toDate">To Date:</label>
          <input
            type="date"
            id="toDate"
            min={fromDate}
            value={toDate}
            onChange={(e) => setToDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="fromTime">From Time:</label>
          <select
            id="fromHour"
            value={fromHour}
            onChange={(e) => setFromHour(e.target.value)}
          >
            {/* Options for hours */}
          </select>
          :
          <select
            id="fromMinute"
            value={fromMinute}
            onChange={(e) => setFromMinute(e.target.value)}
          >
            {/* Options for minutes */}
          </select>
          <select
            id="fromTimePeriod"
            value={fromTimePeriod}
            onChange={(e) => setFromTimePeriod(e.target.value)}
          >
            <option value="AM">AM</option>
            <option value="PM">PM</option>
          </select>
          <input
            type="text"
            value={fromSelectedTime}
            readOnly
            className="selected-time"
          />
        </div>
        <div className="form-group">
          <label htmlFor="toTime">To Time:</label>
          <select
            id="toHour"
            value={toHour}
            onChange={(e) => setToHour(e.target.value)}
          >
            {/* Options for hours */}
          </select>
          :
          <select
            id="toMinute"
            value={toMinute}
            onChange={(e) => setToMinute(e.target.value)}
          >
            {/* Options for minutes */}
          </select>
          <select
            id="toTimePeriod"
            value={toTimePeriod}
            onChange={(e) => setToTimePeriod(e.target.value)}
          >
            <option value="AM">AM</option>
            <option value="PM">PM</option>
          </select>
          <input
            type="text"
            value={toSelectedTime}
            readOnly
            className="selected-time"
          />
        </div>
        <button type="submit">Book Seat</button>
      </form>
    </div>
  );
};

export default BookSeatPage;
