import React, { useState } from "react";
import axios from "axios";

const AddNewUser = () => {
  const [formData, setFormData] = useState({
    associateName: "",
    associateId: "",
    localSystemId: "",
    email: "",
    managerId: "",
    managerEmail: "",
    managerName: "",
    directReport: "",
    company: "",
    opco: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:3000/admin/add-user", formData);
      console.log("User data saved successfully:", response.data);
      // Clear input fields after successful submission
      setFormData({
        associateName: "",
        associateId: "",
        localSystemId: "",
        email: "",
        managerId: "",
        managerEmail: "",
        managerName: "",
        directReport: "",
        company: "",
        opco: ""
      });
    } catch (error) {
      console.error("Error saving user data:", error);
    }
  };

  return (
    <div className="add-user-page">
      <h2 className="add-user-header">Add New User</h2>
      <form onSubmit={handleSubmit} className="add-user-form">
        <div className="addnewuser-group">
          <input
            type="text"
            name="associateName"
            placeholder="Associate Name"
            value={formData.associateName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="text"
            name="associateId"
            placeholder="Associate ID"
            value={formData.associateId}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="text"
            name="localSystemId"
            placeholder="Local System ID"
            value={formData.localSystemId}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="text"
            name="managerId"
            placeholder="Manager ID"
            value={formData.managerId}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="email"
            name="managerEmail"
            placeholder="Manager Email"
            value={formData.managerEmail}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="text"
            name="managerName"
            placeholder="Manager Name"
            value={formData.managerName}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="text"
            name="directReport"
            placeholder="Direct Report"
            value={formData.directReport}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="text"
            name="company"
            placeholder="Company"
            value={formData.company}
            onChange={handleChange}
            required
          />
        </div>
        <div className="addnewuser-group">
          <input
            type="text"
            name="opco"
            placeholder="Opco"
            value={formData.opco}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="submit-button">
          Add User
        </button>
      </form>
    </div>
  );
};

export default AddNewUser;
