import React, { useState } from "react";
import axios from "axios";
import "./AddNewUser.css"; // Import CSS for AddNewUser page

const AddNewUser = () => {
  const [formData, setFormData] = useState({
    associate_name: "",
    associate_id: "",
    localsystemid: "",
    email: "",
    manager_id: "",
    manager_email: "",
    manager_name: "",
    direct_reports: "",
    company: "",
    OpCo: "",
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true); // Set loading to true when submitting the form

    axios
      .post("http://localhost:3000/admin/add-user", formData)
      .then((response) => {
        console.log("User data saved successfully:", response.data);
        setMessage("User added successfully!"); // Set success message
        // Clear input fields after successful submission
        setFormData({
          associate_name: "",
          associate_id: "",
          localsystemid: "",
          email: "",
          manager_id: "",
          manager_email: "",
          manager_name: "",
          direct_reports: "",
          company: "",
          OpCo: "",
        });
      })
      .catch((error) => {
        console.error("Error saving user data:", error);
        setMessage("Error adding user. Please try again."); // Set error message
      })
      .finally(() => {
        setLoading(false); // Set loading back to false after submission
        setTimeout(() => {
          setMessage("");
        }, 3000);
      });
  };

  return (
    <div className="add-user-page">
      <h2 className="add-user-header">Add New User</h2>
      <form onSubmit={handleSubmit} className="add-user-form">
        <div className="form-row">
          <div className="left-column">
            <div className="addnewuser-group">
              <input
                type="text"
                name="associateName"
                placeholder="Associate Name"
                onChange={handleChange}
                required
              />
            </div>
            <div className="addnewuser-group">
              <input
                type="text"
                name="associateId"
                placeholder="Associate ID"
                onChange={handleChange}
                required
              />
            </div>
            <div className="addnewuser-group">
              <input
                type="text"
                name="localSystemId"
                placeholder="Local System ID"
                onChange={handleChange}
                required
              />
            </div>
            <div className="addnewuser-group">
              <input
                type="email"
                name="email"
                placeholder="Email"
                onChange={handleChange}
                required
              />
            </div>
            <div className="addnewuser-group">
              <input
                type="text"
                name="managerId"
                placeholder="Manager ID"
                onChange={handleChange}
                required
              />
            </div>
          </div>
          <div className="right-column">
            <div className="addnewuser-group">
              <input
                type="email"
                name="managerEmail"
                placeholder="Manager Email"
                onChange={handleChange}
                required
              />
            </div>
            <div className="addnewuser-group">
              <input
                type="text"
                name="managerName"
                placeholder="Manager Name"
                value={formData.managerName}
                onChange={handleChange}
                required
              />
            </div>
            <div className="addnewuser-group">
              <input
                type="text"
                name="directReport"
                placeholder="Direct Report"
                value={formData.directReport}
                onChange={handleChange}
                required
              />
            </div>
            <div className="addnewuser-group">
              <input
                type="text"
                name="company"
                placeholder="Company"
                value={formData.company}
                onChange={handleChange}
                required
              />
            </div>
            <div className="addnewuser-group">
              <input
                type="text"
                name="opco"
                placeholder="Opco"
                value={formData.opco}
                onChange={handleChange}
                required
              />
            </div>
          </div>
        </div>
        <div className="submit-row">
          <button type="submit" className="submit-button">
            {loading ? "Adding User..." : "Add User"}
          </button>
        </div>
      </form>
      {message && (
        <div
          className={`message ${message.startsWith("Error") ? "error" : "success"
          }`}
        >
          {message}
        </div>
      )}
    </div>
  );
};

export default AddNewUser;
