// src/components/Admin/UserManagementPage.jsx

import React, { useState, useEffect } from 'react';
import './UserManagementPage.css';
import EditUserPopup from './EditUserPopup'; // Import the EditUserPopup component

const UserManagementPage = () => {
  const [users, setUsers] = useState([]);
  const [showEditPopup, setShowEditPopup] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showDeleteConfirmation, setShowDeleteConfirmation] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [filterRole, setFilterRole] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    // Fetch users from the backend (to be implemented later)
    // For now, use static data
    const staticUsers = [
      { id: 1, firstName: 'John', lastName: 'Doe', email: 'john@example.com', password: 'password1', role: 'DevOps' },
      { id: 2, firstName: 'Jane', lastName: 'Smith', email: 'jane@example.com', password: 'password2', role: 'TechOps' },
      // Add more static user data as needed
    ];
    setUsers(staticUsers);
  }, []);

  const handleEditUser = (user) => {
    setSelectedUser(user);
    setShowEditPopup(true);
  };

  const handleSaveUser = (editedUser) => {
    // Save edited user details to the backend (to be implemented later)
    // For now, update the user in the local state
    setUsers(prevUsers => prevUsers.map(u => u.id === editedUser.id ? editedUser : u));
    setShowEditPopup(false);
  };

  const handleCancelEdit = () => {
    setShowEditPopup(false);
  };

  const handleDeleteUser = (user) => {
    setUserToDelete(user);
    setShowDeleteConfirmation(true);
  };

  const confirmDelete = () => {
    // Delete the user from the local state
    setUsers(prevUsers => prevUsers.filter(u => u.id !== userToDelete.id));
    setShowDeleteConfirmation(false);
  };

  const cancelDelete = () => {
    setShowDeleteConfirmation(false);
  };

  const togglePasswordVisibility = (user) => {
    // Toggle password visibility for the selected user
    const updatedUsers = users.map(u => {
      if (u.id === user.id) {
        return { ...u, passwordVisible: !u.passwordVisible };
      }
      return u;
    });
    setUsers(updatedUsers);
  };

  const filterUsersByRole = (role) => {
    setFilterRole(role);
  };

  const clearFilter = () => {
    setFilterRole('');
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };

  const clearSearch = () => {
    setSearchTerm('');
  };

  const filteredUsers = users.filter(user => {
    return (
      (!filterRole || user.role === filterRole) &&
      (Object.values(user).some(value => String(value).toLowerCase().includes(searchTerm.toLowerCase())))
    );
  });

  return (
    <div className="user-management-container">
      <h2>User Management</h2>
      <div className="filter-container">
        <div className="filter-select">
          <select value={filterRole} onChange={(e) => filterUsersByRole(e.target.value)}>
            <option value="">All Roles</option>
            <option value="DevOps">DevOps</option>
            <option value="TechOps">TechOps</option>
            {/* Add more role options as needed */}
          </select>
          <button onClick={clearFilter}>Clear</button>
        </div>
        <div className="search-container">
          <input type="text" value={searchTerm} onChange={handleSearch} placeholder="Search..." />
          <button onClick={clearSearch}>Clear</button>
        </div>
      </div>
      <table>
        <thead className='tbl-header'>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Password</th>
            <th>Role</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody className='tbl-ontent'>
          {filteredUsers.map(user => (
            <tr key={user.id}>
              <td>{user.id}</td>
              <td>{user.firstName}</td>
              <td>{user.lastName}</td>
              <td>{user.email}</td>
              <td className='pwd-td'>
                {user.passwordVisible ? user.password : '********'}
                <br></br>
                <button onClick={() => togglePasswordVisibility(user)}>Toggle</button>
              </td>
              <td>{user.role}</td>
              <td>
                <button onClick={() => handleEditUser(user)}>Edit</button>
                <button onClick={() => handleDeleteUser(user)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* Render edit user popup if showEditPopup is true */}
      {showEditPopup && (
        <EditUserPopup user={selectedUser} onSave={handleSaveUser} onCancel={handleCancelEdit} />
      )}
      {/* Render delete confirmation popup if showDeleteConfirmation is true */}
      {showDeleteConfirmation && (
        <div className="delete-confirmation">
          <p>Are you sure you want to delete this user?</p>
          <button onClick={confirmDelete}>Yes</button>
          <button onClick={cancelDelete}>No</button>
        </div>
      )}

    </div>
  );
};

export default UserManagementPage;
